import React, { useState } from "react";
import AddBar from "./AddBar";
import AddButton from "./AddButton";

function ToDoList(props){

    const [titleCreated, setTitleCreated] = useState(false);

    const [note, setNote] = useState({
        title: "",
        items: []
    })

    const [item, setItem] = useState("");

    function makeItem(event){
        setItem(event.value)
    }

    function makeTitle(event){
        const newTitle = event.target.value;
        setNote(prevNote => {
            return {
                title: newTitle,
                ...prevNote
            }
        });
    }

    function createTitle(event){
        const newValue = event.target.value;
        setTitleCreated(true);
    }

    return(
    <div className="to-do-list">
        { ((props.isTitle && !titleCreated) || !props.isTitle) ? 
        <span style={{display:"inline-block"}}>
            <AddButton handleAdded={createTitle}/>
            <form>
                <input
                    onChange={makeTitle}
                    name="title"
                    placeholder="Add a title..."
                /> 
         </form>
        </span> : <h3>{note.title}</h3>}

        <br></br>


        <ul>
            <li>Item 1</li>
            <li>Item 2</li>
        </ul>

    </div>);

}

export default ToDoList;
