    const [notes, setNotes] = useState([]);

    function addNote(newTitle, newItems){
        setNotes( prevNotes => {
            return ([...prevNotes, { title: newTitle, items: newItems}]);
        })
    }


    return <div id="stage-container">
        <div id="content-wrap">
            <Header />

            <ToDoList />
