import React, { useState } from "react";
import AddButton from "./AddButton";

function AddBar(props){

    const [titleCreated, setTitleCreated] = useState(true);
    return <div>
    { ((props.isTitle && !titleCreated) || !props.isTitle) ? 
    <span style={{display:"inline-block"}}>
        <AddButton handleAdded={console.log("hit button")}/>
        <form>
            <input
                onChange={props.makeItem}
                name={props.name}
                placeholder={props.placeholder}
            /> 
        </form>
    </span> : <h3>{props.title}</h3>}
</div>   
}

export default AddBar;
