import React, { useState } from "react";
import AddBar from "./AddBar";
import AddButton from "./AddButton";

function ToDoList(props){

    const [titleCreated, setTitleCreated] = useState(false);

    const [note, setNote] = useState({
        title: "",
        items: []
    })

    const [item, setItem] = useState("");

    function makeItem(event){
        const newItem = event.target.value;
        setItem(event.value)
        setNote((prevNote) => {
            return {
                ...prevNote,

            }

        })
    }

    function makeTitle(event){
        const newTitle = event.target.value;
        setNote(prevNote => {
            return {
                ...prevNote,
                title: newTitle
            }
        });
    }

    function createTitle(event){
        if (titleCreated == false){
            const newValue = event.target.value;
            setTitleCreated(true);
        } else {
            setTitleCreated(false);
            setNote(prevNote => {
                return {
                    ...prevNote,
                    title: ""
                }
            });
        }

    }

    return(
    <div className="to-do-list">
        { (!titleCreated) ? 
        <span style={{display:"inline-block"}}>
            <AddButton delete={false} handleAdded={createTitle}/>
            <form>
                <input
                    onChange={makeTitle}
                    name="title"
                    placeholder="Add a title..."
                /> 
         </form>
        </span> : <span>
            <AddButton delete={true} handleAdded={createTitle}/>
            <h3>{note.title}</h3>
        </span>}

        <br></br>

        { <span style={{display:"inline-block"}}>
            <AddButton delete={false} handleAdded={createTitle}/>
            <form>
                <input
                    onChange={makeItem}
                    name="item adder"
                    placeholder="Next item..."
                /> 
         </form>
        </span> 

        }


        <ul>
            <li>Item 1</li>
            <li>Item 2</li>
        </ul>

    </div>);

}

export default ToDoList;
